int main ()
{
    int n;
    cin >> n;
    double times[] = {0.0, 0.0}, dis[] = {0.0, 0.0};
    vector<double> f(n), a(n), x(n), y(n);
    vector<int> t(n);
    for (int i = 0; i < n; i++) {
        cin >> f[i] >> a[i] >> t[i] >> x[i] >> y[i];
    }

    bool flag[] = {true, true};

    for (int i = 1; i < n; i++) {
        double dd = ((x[i] - x[i-1]) * (x[i] - x[i-1]) + (y[i] - y[i-1]) * (y[i] - y[i-1]));
        double tt = f[i] - f[i-1];
        if (t[i] != t[i-1]) continue;
        if (dd > dis[t[i]]) {
            dis[t[i]] = dd;
            times[t[i]] = f[i] - f[i-1];
            flag[t[i]] = false;
        } else if (dd == dis[t[i]] && tt <  times[t[i]]) {
            times[t[i]] = tt;
        }
    }

    if (flag[0]) {
        cout << -1 << " " << -1 << endl;
    } else {
        cout << sqrt(dis[0]) << " " << times[0] / 60.0 << endl;
    }
    if (flag[1]) {
        cout << -1 << " " << -1 << endl;
    } else {
        cout << sqrt(dis[1]) << " " << times[1] / 60.0 << endl;
    }

    return 0;
}

