          A[t].ti = f - A[t].f;
        }
        else if(abs(A[t].di- A[t].dis(x,y)) <= eps){
          if(A[t].ti > f - A[t].f){
            A[t].ti = f - A[t].f;
          }
        }
      }
      bfr = t;
      A[t] = {f,a,x,y,true,A[t].ti,A[t].di};
    }
  }
  rep(i,2){
    if(A[i].di == 0){
      cout << -1 << ' ' << -1 << endl;
    }
    else
      fcout(10) << A[i].di << ' ' << A[i].ti/60.0 << endl;
  }
  return 0;
}

