double F0[100], X0[100], Y0[100];
int A0[100], T0[100];

int main(){
    int N;
    scanf("%d", &N);

    for(int i=0;i<N;i++){
        scanf("%lf %d %d %lf %lf", F0+i, A0+i, T0+i, X0+i, Y0+i);
    }

    double longest[2] = {-1e10, -1e10}, shortest[2] = {0.0, 0.0};
    for(int i=0;i<N;i++){
        for(int j=i+1;j<N;j++){
            if(T0[i] != T0[j] || A0[i] == A0[j]){continue;}

            double t = F0[j] - F0[i],
                dx = X0[j] - X0[i],
                dy = Y0[j] - Y0[i],
                dist = std::sqrt(dx*dx + dy*dy);

            if(dist > longest[T0[i]]){
                longest[T0[i]] = dist;
                shortest[T0[i]] = t;
            }else if(std::abs(dist - longest[T0[i]]) < 1e-8 && t < shortest[T0[i]]){
                shortest[T0[i]] = t;
            }
        }
    }

    for(int i=0;i<2;i++){
        if(longest[i] < 0){puts("-1 -1"); continue;}
        printf("%.6f %.6f\n", longest[i], shortest[i] / 60);
    }
}

