int main()
{
	cin.tie( 0 );
	ios::sync_with_stdio( false );

	int N;
	cin >> N;

	VI fs( N ), as( N ), ts( N ), xs( N ), ys( N );
	REP( i, N )
	{
		cin >> fs[i] >> as[i] >> ts[i] >> xs[i] >> ys[i];
	}

	VT( double ) dists( 2, -1 ), times( 2, 1e9 );
	REP( i, 1, N )
	{
		const int t = ts[i];
		if ( !( ts[ i - 1 ] == t && as[i] != as[ i - 1 ] ) )
		{
			continue;
		}

		const double dist = sqrt( pow( xs[i] - xs[ i - 1 ], 2. ) + pow( ys[i] - ys[ i - 1 ], 2. ) );
		const double time = ( fs[i] - fs[ i - 1 ] ) * ( 1 / 60. );

		if ( dists[t] < dist || abs( dists[t] - dist ) <= 1e-5 && time < times[t] )
		{
			dists[t] = dist;
			times[t] = time;
		}
	}

	cout << setprecision( 8 ) << fixed;
	REP( i, 2 )
	{
		if ( dists[i] == -1 )
		{
			cout << "-1 -1" << endl;
		}
		else
		{
			cout << dists[i] << ' ' << times[i] << endl;
		}
	}

	return 0;
}

