	rep (i, n ){
		cin >> f[i] >> a[i] >> t[i] >> x[i] >> y[i];
	} // end rep

	rep (i, 2 ) res[i] = -1.;
	rep (i, 2 ) mint[i] = INF;
	rep (i, n ){
		if (i != n - 1 && t[i] == t[i+1] && a[i] != a[i+1] ){
			int tt = t[i];
			double len = calc_dis (x[i], y[i], x[i+1], y[i+1] );
			int diff = f[i+1] - f[i];
			if (res[tt] < len ){
				res[tt] = len;
				mint[tt] = diff;
			}else
			if (abs(res[tt] - len ) < EPS ){
				res[tt] = len;
				mint[tt] = min (mint[tt], diff );
			} // end if
		} // end if
	} // end rep

	rep (i, 2 ){
		if (res[i] == -1. ){
			cout << -1 << ' ' << -1 << endl;
		}else{
			printf ("%.6lf %.6lf\n", res[i], (double)mint[i]/60. );
		} // end if
	} // end rep

	return 0;
}

