struct q{
  int f, a, x, y;
};

int main() {
  int N;
  vector< q > Query[2];
  cin >> N;
  for(int i = 0; i < N; i++){
    int f, a, t, x, y;
    cin >> f >> a >> t >> x >> y;
    Query[t].push_back((q){f, a, x, y});
  }
  int A[2] = {-1,-1}, B[2] = {-1, -1};
  for(int k = 0; k < 2; k++){ 
    for(int i = 1; i < Query[k].size(); i++) {
      q& prev = Query[k][i - 1], now = Query[k][i];
      if(prev.a == now.a) continue;
      if(A[k] < pow(abs(prev.x - now.x), 2) + pow(abs(prev.y - now.y), 2)) {
        A[k] = pow(abs(prev.x - now.x), 2) + pow(abs(prev.y - now.y), 2);
        B[k] = now.f - prev.f;
      }
      if((A[k] == pow(abs(prev.x - now.x), 2) + pow(abs(prev.y - now.y), 2)) && B[k] > now.f - prev.f) {
        B[k] = now.f - prev.f;
      }
    }
  }
  cout << fixed << setprecision(10);
  for(int k = 0; k < 2; k++) {
    if(A[k] == -1) {
      cout << -1 << " " << -1 << endl;
    } else {
      cout << sqrt(A[k]) << " " << (double)B[k] / 60 << endl;
    }
  }

}

