int main(){
    cin >> n;
    f.resize(n);
    a.resize(n);
    t.resize(n);
    x.resize(n);
    y.resize(n);

    REP(i, n){
        cin >> f[i] >> a[i] >> t[i] >> x[i] >> y[i];
    }

    pair<int, int> pre;
    double max_pass_0 = -1, max_pass_1 = -1, max_time_0 = -1, max_time_1 = -1;
    pre.first = t[0]; pre.second = a[0];
    FOR(i, 1, n){
        if(pre.first == t[i] && pre.second != a[i]){
            double dis = sqrt(pow(abs(x[i-1] - x[i]), 2) + pow(abs(y[i-1] - y[i]), 2));
            double times = (f[i] - f[i-1])/60.0;
            if(t[i] == 0){
                if(max_pass_0 < dis){
                    max_pass_0 = dis;
                    max_time_0 = times;
                }else if(max_pass_0 == dis){
                    if(max_time_0 == -1 || max_time_0 > times) max_time_0 = times;
                }
            }else{
                if(max_pass_1 < dis){
                    max_pass_1 = dis;
                    max_time_1 = times;
                }else if(max_pass_1 == dis){
                    if(max_time_1 == -1 || max_time_1 > times) max_time_1 = times;
                }
            }
        }
        pre.first = t[i]; pre.second = a[i];
    }

    cout << max_pass_0 << " " << max_time_0 << endl;
    cout << max_pass_1 << " " << max_time_1 << endl;
}

