			if(f1 != -1){
				if(a1 != a){
					int d = pow(x - x1, 2.0) + pow(y - y1, 2.0);
					int s = f - f1;
					if(ans1_d < d){
						ans1_d = d;
						ans1_s = s;
					}
					else if(d == ans1_d){
						ans1_s = min(ans1_s, s);
					}
				}
			}
			f1 = f;
			a1 = a;
			x1 = x;
			y1 = y; 
		}
		else{
			if(f2 != -1){
				if(a2 != a){
					int d = pow(x - x2, 2.0) + pow(y - y2, 2.0);
					int s = f - f2;
					if(ans2_d < d){
						ans2_d = d;
						ans2_s = s;
					}
					else if(ans2_d == d){
						ans2_s = min(ans2_s, s);
					}
				}
			}
			f2 = f;
			a2 = a;
			x2 = x;
			y2 = y; 
		}
