#include <cmath>
#include <iostream>
using namespace std;
int n, f[100], a[100], t[100], x[100], y[100];
int main() {
	cin >> n;
	for (int i = 0; i < n; i++) cin >> f[i] >> a[i] >> t[i] >> x[i] >> y[i];
	int r1 = -1, r2 = -1, r3 = -1, r4 = -1;
	for (int i = 1; i < n; i++) {
		if (a[i - 1] == a[i] || t[i - 1] != t[i]) continue;
		int dist = (x[i] - x[i - 1]) * (x[i] - x[i - 1]) + (y[i] - y[i - 1]) * (y[i] - y[i - 1]); 
		if (t[i] == 0) {
			if (dist > r1) r1 = dist, r2 = f[i] - f[i - 1];
			else if (dist == r1 && f[i] - f[i - 1] < r2) r2 = f[i] - f[i - 1];
		}
		else {
			if (dist > r3) r3 = dist, r4 = f[i] - f[i - 1];
			else if (dist == r3 && f[i] - f[i - 1] < r4) r4 = f[i] - f[i - 1];
		}
	}
	printf("%.9f %.9f\n", sqrt(r1), r2 / 60.0);
	printf("%.9f %.9f\n", sqrt(r3), r4 / 60.0);
	return 0;
}

