};

/* global variables */

Log ls[MAX_N];
double maxds[2], minss[2];

/* subroutines */

/* main */

int main() {
  int n;
  cin >> n;
  for (int i = 0; i < n; i++)
    cin >> ls[i].f >> ls[i].a >> ls[i].t >> ls[i].x >> ls[i].y;

  maxds[0] = maxds[1] = -1.0;
  minss[0] = minss[1] = 0.0;

  for (int i = 1; i < n; i++) {
    Log &l0 = ls[i - 1], &l1 = ls[i];

    if (l0.t == l1.t && l0.a != l1.a) {
      double dx = l1.x - l0.x, dy = l1.y - l0.y;
      double d = sqrt(dx * dx + dy * dy);
      double s = (l1.f - l0.f) / 60.0;

      if (maxds[l0.t] < d) {
	maxds[l0.t] = d;
	minss[l0.t] = s;
      }
      else if (maxds[l0.t] == d && minss[l0.t] > s)
	minss[l0.t] = s;
    }
  }

  for (int i = 0; i < 2; i++) {
    if (maxds[i] < 0.0) puts("-1 -1");
    else printf("%.8lf %.8lf\n", maxds[i], minss[i]);
  }

  return 0;
}

