const int INF = 1e+9;
double eps = 1e-10;

int main() {
	int n, f, f1 = -1, f2 = -1, a, a1 = 0, a2 = 0, t, x, y, x1, y1, x2, y2;
	double ans1_d = -1, ans1_s = INF, ans2_d = -1, ans2_s = INF;
	cin >> n;
	for(int i = 0; i < n; ++i){
		scanf("%d%d%d%d%d", &f, &a, &t, &x, &y);
		if(t == 0){
			if(f1 != -1){
				if(a1 != a){
					double d = sqrt((double)pow((double)x - x1, 2.0) + (double)pow((double)y - y1, 2.0));
					double s = (f - f1) / 60.0;
					if(ans1_d < d){
						ans1_d = d;
						ans1_s = s;
					}
					else if(fabs(ans1_d - d) < eps){
						ans1_s = min(ans1_s, s);
					}
				}
			}
			f1 = f;
			a1 = a;
			x1 = x;
			y1 = y; 
		}
		else{
			if(f2 != -1){
				if(a2 != a){
					double d = sqrt((double)pow((double)x - x2, 2.0) + (double)pow((double)y - y2, 2.0));
					double s = (f - f2) / 60.0;
					if(ans2_d < d){
						ans2_d = d;
						ans2_s = s;
					}
					else if(fabs(ans2_d - d) < eps){
						ans2_s = min(ans2_s, s);
					}
				}
			}
			f2 = f;
			a2 = a;
			x2 = x;
			y2 = y; 
		}
	}
	if(ans1_d == -1)
		cout << "-1" << " " << "-1" << endl;
	else
		cout << fixed << ans1_d << " " << ans1_s << endl;
	if(ans2_d == -1)
		cout << "-1" << " " << "-1" << endl;
	else
		cout << fixed << ans2_d << " " << ans2_s << endl;
	return 0;
}

