			if(t[i] == 0){
				if(da == d && fa > df){
					fa = df;
				}
				else if(da <  d){
					da = d;
					fa = df;
				}
			}else if(t[i] == 1){
				if(db == d && fb > df){
					fb = df;
				}
				else if(db < d){
					db = d;
					fb = df;
				}
			}
		}
	}
	if(da == -1){cout << -1 << " " << -1 << endl;}
	else cout << fixed << setprecision(10) << sqrt(da) << " " << 1.*fa/60. << endl;
	if(db == -1){cout << -1 << " " << -1 << endl;}
	else cout << fixed << setprecision(10) << sqrt(db) << " " << fb/60. << endl;

	return 0;
}

