			int diff = f[i+1] - f[i];
			if (res[tt] < len ){
				res[tt] = len;
				mint[tt] = diff;
			}else
			if (res[tt] == len ){
				mint[tt] = min (mint[tt], diff );
			} // end if
		} // end if
	} // end rep

	rep (i, 2 ){
		if (res[i] == -1 ){
			cout << -1 << ' ' << -1 << endl;
		}else{
			printf ("%.6lf %.6lf\n", sqrt(res[i] ), (double)mint[i]/60. );
		} // end if
	} // end rep

	return 0;
}

