void solver() {
    int n = input();
    vector< int > fs, as, ts, xs, ys;
    range(i, 0, n) {
        int f, a, t, x, y;
        cin >> f >> a >> t >> x >> y;
        fs.push_back(f);
        as.push_back(a);
        ts.push_back(t);
        xs.push_back(x);
        ys.push_back(y);
    }

    auto cou = [&](int t) {
        i64 dist = -1, time;
        range(i, 1, n) {
            if (as[i - 1] == as[i]) continue;
            if (ts[i] != t || ts[i - 1] != t) continue;
            i64 d = (xs[i] - xs[i - 1]) * (xs[i] - xs[i - 1]) + (ys[i] - ys[i - 1]) * (ys[i] - ys[i - 1]);
            if (d == dist) {
                chmin(time, fs[i] - fs[i - 1]);
            } else if (d > dist) {
                dist = d;
                time = fs[i] - fs[i - 1];
            }
        }

        if (dist == -1) {
            cout << -1 << " " << -1 << endl;
        } else {
            cout << fixed << setprecision(20);
            cout << sqrt(dist) << " " << (time / 60.0) << endl;
        }
    };

    cou(0);
    cou(1);
}

signed main(int argc, char *argv[]) {
    solver();
}

