    template<class T> ostream& operator<<(ostream& os, const vector<T>& vs) {
        if (vs.empty()) return os << "[]";
        os << "[" << vs[0];
        for (int i = 1; i < vs.size(); i++) os << " " << vs[i];
        return os << "]";
    }
    template<class T> istream& operator>>(istream& is, vector<T>& vs) {
        for (auto it = vs.begin(); it != vs.end(); it++) is >> *it;
        return is;
    }

    struct P {
        int f, a, t, x, y;
    };
    istream& operator>>(istream& is, P& p) {
        return is >> p.f >> p.a >> p.t >> p.x >> p.y;
    }

    ll d2(int x, int y, int ax, int ay) {
        return (x - ax) * (x - ax) + (y - ay) * (y - ay);
    }

    int N;
    void solve() {
        cin >> N;
        P prev; cin >> prev;
        ll dist2[2] = {0};
        ll time[2] = {0};
        for (int q = 1; q < N; q++) {
            P cur; cin >> cur;
            int t = cur.t;
            if (prev.t == cur.t && prev.a != cur.a) {
                ll d = d2(cur.x, cur.y, prev.x, prev.y);
                if (d > dist2[t] || (d == dist2[t] && time[t] > cur.f - prev.f)) {
                    dist2[t] = d;
                    time[t] = cur.f - prev.f;
                }
            }
            prev = cur;
        }
        cout << fixed << setprecision(12);
        for (int t = 0; t < 2; t++) {
            if (dist2[t] == 0) {
                cout << -1 << " " << -1 << endl;
            } else {
                cout << sqrtl(real(dist2[t])) << " " << real(time[t]) / 60.0 << endl;
            }
        }
    }
}

