		const double time = ( fs[i] - fs[ i - 1 ] ) * ( 1 / 60. );

		if ( dists[t] < dist || time < times[t] )
		{
			dists[t] = dist;
			times[t] = time;
		}
	}

	cout << setprecision( 8 ) << fixed;
	REP( i, 2 )
	{
		if ( 0 < dists[i] )
		{
			cout << dists[i] << ' ' << times[i] << endl;
		}
		else
		{
			cout << "-1 -1" << endl;
		}
	}

	return 0;
}

