public class Main {
	public static void main(String[] args) throws NumberFormatException,
			IOException {
		ContestScanner in = new ContestScanner();
		int n = in.nextInt();
		int[] dmax = new int[2];
		int oldx = 0;
		int oldy = 0;
		int[] fmin = new int[2];
		fmin[0] = 1000000;
		fmin[1] = 1000000;
		int oldt = -1;
		int olda = -1;
		int oldf = 0;
		for(int i=0; i<n; i++){
			int f = in.nextInt();
			int a = in.nextInt();
			int t = in.nextInt();
			int x = in.nextInt();
			int y = in.nextInt();
			int d = (x-oldx)*(x-oldx) + (y-oldy)*(y-oldy);
			if(oldt != t || olda == a){
				oldt = t;
				olda = a;
				oldf = f;
				oldx = x;
				oldy = y;
				continue;
			}
			oldt = t;
			olda = a;
			if(dmax[t] < d){
				dmax[t] = d;
				fmin[t] = f - oldf;
			}else if(dmax[t] == d){
				if(fmin[t] > f - oldf) fmin[t] = f - oldf;
			}
			oldf = f;
			oldx = x;
			oldy = y;
		}
		double[] d = new double[2];
		double[] f = new double[2];
		for(int i=0; i<2; i++){
			if(dmax[i] == 0){
				System.out.println("-1 -1");
			}else{
				d[i] = Math.sqrt(dmax[i]);
				f[i] = fmin[i]/60.0;
				System.out.println(d[i]+" "+f[i]);
			}
		}
	}

}

