class IO extends PrintWriter {
	private final InputStream in;
	private final byte[] buffer = new byte[1024];
	private int ptr = 0;
	private int buflen = 0;

	public IO() { this(System.in);}
	public IO(InputStream source) { super(System.out); this.in = source;}
	private boolean hasNextByte() {
		if (ptr < buflen) {
			return true;
		}else{
			ptr = 0;
			try {
				buflen = in.read(buffer);
			} catch (IOException e) {
				e.printStackTrace();
			}
			if (buflen <= 0) {
				return false;
			}
		}
		return true;
	}
	private int readByte() { if (hasNextByte()) return buffer[ptr++]; else return -1;}
	private static boolean isPrintableChar(int c) { return 33 <= c && c <= 126;}
	private static boolean isNewLine(int c) { return c == '\n' || c == '\r';}
	private void skipUnprintable() { while(hasNextByte() && !isPrintableChar(buffer[ptr])) ptr++;}
	private void skipNewLine() { while(hasNextByte() && isNewLine(buffer[ptr])) ptr++;}
	public boolean hasNext() { skipUnprintable(); return hasNextByte();}
	public boolean hasNextLine() { skipNewLine(); return hasNextByte();}
	public String next() {
		if (!hasNext()) {
			throw new NoSuchElementException();
		}
		StringBuilder sb = new StringBuilder();
		int b = readByte();
		while(isPrintableChar(b)) {
			sb.appendCodePoint(b);
			b = readByte();
		}
		return sb.toString();
	}
	public char[] nextCharArray(int len) {
		if (!hasNext()) {
			throw new NoSuchElementException();
		}
		char[] s = new char[len];
		int i = 0;
		int b = readByte();
		while(isPrintableChar(b)) {
			if (i == len) {
				throw new InputMismatchException();
			}
			s[i++] = (char) b;
			b = readByte();
		}
		return s;
	}
	public String nextLine() {
		if (!hasNextLine()) {
			throw new NoSuchElementException();
		}
		StringBuilder sb = new StringBuilder();
		int b = readByte();
		while(!isNewLine(b)) {
			sb.appendCodePoint(b);
			b = readByte();
		}
		return sb.toString();
	}
	public long nextLong() {
		if (!hasNext()) {
			throw new NoSuchElementException();
		}
		long n = 0;
		boolean minus = false;
		int b = readByte();
		if (b == '-') {
			minus = true;
			b = readByte();
		}
		if (b < '0' || '9' < b) {
			throw new NumberFormatException();
		}
		while(true){
			if ('0' <= b && b <= '9') {
				n *= 10;
				n += b - '0';
			}else if(b == -1 || !isPrintableChar(b)){
				return minus ? -n : n;
			}else{
				throw new NumberFormatException();
			}
			b = readByte();
		}
	}
	public int nextInt() {
		long nl = nextLong();
		if (nl < Integer.MIN_VALUE || nl > Integer.MAX_VALUE) {
			throw new NumberFormatException();
		}
		return (int) nl;
	}
	public char nextChar() {
		if (!hasNext()) {
			throw new NoSuchElementException();
		}
		return (char) readByte();
	}
	public double nextDouble() { return Double.parseDouble(next());}
	public int[] arrayInt(int n) { int[] a = new int[n]; for(int i=0;i<n;i++) a[i] = nextInt(); return a;}
	public long[] arrayLong(int n) { long[] a = new long[n]; for(int i=0;i<n;i++) a[i] = nextLong(); return a;}
	public double[] arrayDouble(int n) { double[] a = new double[n]; for(int i=0;i<n;i++) a[i] = nextDouble(); return a;}
	public void arrayInt(int[]... a) {for(int i=0;i<a[0].length;i++) for(int j=0;j<a.length;j++) a[j][i] = nextInt();}
	public int[][] matrixInt(int n,int m) { int[][] a = new int[n][]; for(int i=0;i<n;i++) a[i] = arrayInt(m); return a;}
	public char[][] charMap(int n,int m) { char[][] a = new char[n][]; for(int i=0;i<n;i++) a[i] = nextCharArray(m); return a;}
	public void close() {
		super.close();
		try {
			in.close();
		} catch (IOException e) {}
	}
}

import java.awt.geom.Point2D;
import java.util.ArrayList;
import java.util.Collections;
import java.util.Scanner;

public class Main {
	Scanner in = new Scanner(System.in);
	public static void main(String[] args) {
		new Main();
	}
	public Main() {
		new RitProA().doIt();
	}
	class RitProA{
		void doIt(){
			int n = in.nextInt();
			ArrayList<Frame> list = new ArrayList<Frame>();
			for(int i=0;i<n;i++)list.add(new Frame(in.nextInt(),in.nextInt(), in.nextInt(), in.nextInt(), in.nextInt()));
			Collections.sort(list);

			double maxKyori1 = -1.000000;
			int minTime1 = Integer.MAX_VALUE/2;
			double maxKyori2 = -1.000000;
			int minTime2 = Integer.MAX_VALUE/2;
			for(int i=0;i<n-1;i++){
				Frame f1 = list.get(i);
				Frame f2 = list.get(i+1);
				Point2D p1 = new Point2D.Double(f1.x,f1.y);
				Point2D p2 = new Point2D.Double(f2.x,f2.y);
				if(f1.t!=f2.t)continue;
				if(f1.t == 0){
					if(p1.distance(p2) > maxKyori1 && f1.a != f2.a){
						maxKyori1 = p1.distance(p2)*1.000000;
						minTime1 = f2.f - f1.f;
					}else if(Math.abs(p1.distance(p2) - maxKyori1) < 0.00000000001){
						maxKyori1 = p1.distance(p2)*1.000000;
						minTime1 = f2.f - f1.f;
					}
				}else{
					if(p1.distance(p2) >= maxKyori2 && f1.a != f2.a){
						maxKyori2 = p1.distance(p2)*1.000000;
						minTime2 = f2.f - f1.f;
					}else if(Math.abs(p1.distance(p2) - maxKyori2) < 0.00000000001){
							maxKyori2 = p1.distance(p2)*1.000000;
							minTime2 = f2.f - f1.f;
					}
				}
			}
			if(maxKyori1 == -1.0){
				System.out.println("-1 -1");
			}else System.out.printf("%.5f %.5f\n",maxKyori1,minTime1/60.0);
			if(maxKyori2 == -1.0)System.out.println("-1 -1");
			else System.out.printf("%.5f %.5f\n",maxKyori2,minTime2/60.0);
		}
	}

	class Frame implements Comparable<Frame>{
		int a,t,x,y,f;
		public Frame(int _f,int _a,int _t,int _x,int _y){
			this.f = _f;
			this.x = _x;
			this.y = _y;
			this.a = _a;
			this.t = _t;
		}
		public int compareTo(Frame f) {
			return this.f - f.f;
		}


	}
}

