import java.io.IOException;
import java.util.InputMismatchException;

public class Main {
	void run() {
		MyScanner sc = new MyScanner();

		int n = sc.nextInt();
		int pf = sc.nextInt();
		int pa = sc.nextInt();
		int pt = sc.nextInt();
		int px = sc.nextInt();
		int py = sc.nextInt();

		double[] max = new double[2];
		int[] time = new int[2];
		max[0] = max[1] = 0;
		time[0] = time[1] = 1 << 20;
		for (int i = 1; i < n; i++) {
			int f = sc.nextInt();
			int a = sc.nextInt();
			int t = sc.nextInt();
			int x = sc.nextInt();
			int y = sc.nextInt();

			if (pt == t && pa != a) {
				double len = Math.hypot(Math.abs(px - x), Math.abs(py - y));
				if (max[t] < len) {
					max[t] = len;
					time[t] = f - pf;
				} else if (max[t] == len && time[t] > f - pf) {
					time[t] = f - pf;
				}
			}
			pf = f;
			pa = a;
			pt = t;
			px = x;
			py = y;
		}

		if (max[0] > 0) {
			System.out.println(max[0] + " " + 1. * time[0] / 60);
		} else {
			System.out.println(-1 + " " + -1);
		}
		if (max[1] > 0) {
			System.out.println(max[1] + " " + 1. * time[1] / 60);
		} else {
			System.out.println(-1 + " " + -1);
		}
	}

	public static void main(String[] args) {
		new Main().run();
	}

