vector<string> code_convert(vector<string> airports){
    vector<string> codes(airports.size());
    REP(i,0,airports.size()){
        int idx=0;
        codes[i].push_back(airports[i][0]);
        while (idx < airports[i].size()){
            if(boin(airports[i][idx])) codes[i].push_back(airports[i][idx+1]);
            idx++;
        }
    }
    return codes;
}

//入力された２つの文字列のk番目までが同じだったらtrueを返す
bool code_judge(string A, string B, int k){
    bool flag=true;
    if(min(A.size(),B.size()) < k){
        if(A.size()==B.size()){
            REP(i,0,A.size()) if(A[i]!=B[i]) flag=false;
        }else{
            return false;
        }
    }else{
        REP(i,0,k) if(A[i]!=B[i]) flag=false;
    }
    return flag;
}

