bool bs(int now, const vector<string> &full) {
  map<string, int> m;
  int n = SZ(full);
  bool ok = true;
  REP(i, n) {
    if(m[full[i].substr(0, now)]++ >= 1) {
      ok = false;
      break;
    }
  }
  return ok;
}

