    string vowel = "aiueo";
    int sz = 0;
    for (int i = 0; i < n; ++i) {
        string s, t;
        cin >> s;
        t += s[0];
        for (int j = 0; j+1 < s.size(); ++j) {
            for (int k = 0; k < 5; ++k) {
                if(s[j] == vowel[k]) t += s[j+1];
            }
        }
        v[i] = t;
        sz = max(sz, (int)t.size());
    }
    sort(v.begin(), v.end());
    v.erase(unique(v.begin(), v.end()), v.end());
