	while(1){
		scanf("%d",&n);
		if(n==0)break;
		memset(flag,true,sizeof(flag));
		memset(used,false,sizeof(used));
		for(int i=0;i<=50;i++){
			data[i].clear();
		}
		for(int i=0;i<n;i++){
			string s;
