			string c="";
			for(int j=0;j<s.size();j++){
				c+=s[j];
				while(j<s.size() && s[j]!='a' && s[j]!='i' && s[j]!='u' && s[j]!='e' && s[j]!='o')j++;
				used[c.size()]=true;
				if(data[c.size()].find(c)!=data[c.size()].end())flag[c.size()]=false;
				data[c.size()].insert(c);
			}
			for(int k=c.size()+1;k<=50;k++){
				used[k]=true;
				if(data[k].find(c)!=data[k].end())flag[k]=false;
				data[k].insert(c);
			}
		}
		int res=-1;
		for(int i=50;i>=0;i--){
			if(flag[i] && used[i])res=i;
		}
		printf("%d\n",res);
	}
	return 0;
