{

    switch (c)
    {
    case 'a':
        return true;
    case 'e':
        return true;
    case 'i':
        return true;
    case 'o':
        return true;
    case 'u':
        return true;
    default:
        return false;
    }
}

string conv(string name)
{
    string buf;
    bool flag = 1;
    for (const auto &c : name)
    {
        if (flag)
        {
            buf.push_back(c);
        }
        flag = isVowel(c) ? 1 : 0;
    }
    return buf;
}

bool substr_eq(string s1, string s2, int k)
{
    int m = min(k, (int)min(s2.size(), s1.size()));

    for (int i = 0; i < m; ++i)
    {
        if (s1[i] != s2[i])
            return false;
    }
    return !(m < k);
}

bool check(vector<string> list, int k)
{
    for (int i = 0; i < list.size() - 1; ++i)
    {
        for (int j = i + 1; j < list.size(); ++j)
        {
            if (substr_eq(list[i], list[j], k))
            {
                return 0;
            }
        }
    }
    return 1;
}

int main()
{
    int n;
    while (cin >> n && n != 0)
    {
        vector<string> list(n);

        for (auto &name : list)
        {
            cin >> name;
            name = conv(name);
            //cout << name << endl;
        }

        int M = 0;
        for (const auto &str : list)
        {
            M = max(M, (int)str.size());
        }

        int k = 1;
        for (; k < M; ++k)
        {
            if (check(list, k))
            {
                break;
            }
        }

        cout << (k < M ? k : -1) << endl;
    }
    return 0;
}

