int main(){
	vector <string> Code;
	int i, j, k, n, max;
	while (1){
		cin >> n;
		if (n == 0) break;
		Code.clear();
		for (k = 0; k < n; k++){
			string temp, code;
			cin >> temp;
			code += temp[0];
			for (i = 0; i < temp.size(); i++){
				if (temp[i] == 'a' || temp[i] == 'i' || temp[i] == 'u' || temp[i] == 'e' || temp[i] == 'o'){
					code += temp[i + 1];
				}
			}
			Code.push_back(code);
			if (k == 0) max = code.size();
			else if (code.size() > max) max = code.size();
		}
		for (k = 0;k < max; k++){
			for (i = 0; i < n - 1; i++){
				for (j = i + 1; j < n; j++){
					if (k >= Code[i].size() || k >= Code[j].size()) continue;
					if (Code[i][k] == Code[j][k]) break;
				}
				if (j < n - 1) break;
			}
			if (i == n - 1 && j == n) break;
		}
		if(k==max) cout << -1 << endl;
		else cout << k + 1 << endl;
	}
