const int MOD  = (int)1e9 + 7;
const int INF  = (int)1e9 + 1;
const ll  LINF = (ll)1e18 + 1;

string decode(string S) {
    set<char> set{'a', 'i', 'u', 'e', 'o'};

    size_t index = 0;
    string ret = "";
    while(index < S.size() - 1) {
        auto itr = set.find(S[index]);
        if(itr != set.end()) {
            ret += S[index + 1];
        }
        index++;
    }
    return ret;
}

int main() {
    cin.tie(0);
    ios::sync_with_stdio(false);

    while(true) {
        int N;
        std::cin >> N;
        if(N == 0) break;

        size_t max_decoded = 0;
        vector<string> S(N);
        REP(i, N) {
            string s;
            std::cin >> s;
            S[i] = s.front() + decode(s);
            max_decoded = max(max_decoded, S[i].size());
        }

        size_t code_length = 1;
        while(true) {
            set<string> set;
            REP(i, N) {
                auto s = S[i].substr(0, code_length);
                auto itr = set.find(s);
                if(itr == set.end()) {
                    set.insert(s);
                }
                else {
                    break;
                }
            }

            if(set.size() == S.size()) {
                std::cout << code_length << std::endl;
                break;
            }
            else if(code_length == max_decoded) {
                std::cout << -1 << std::endl;
                break;
            }
            code_length++;
        }
    }

    return 0;
}

