int main(){
	int n;
	while(cin>>n){
		if(n==0) break;
		string in[n];
		for(int i=0;i<n;i++){
			cin>>in[i];
		}
		int mojisu=0,ct=0;
		while(ct<(n*(n-1))/2){
			int kazu1[n];
			for(int i=0;i<n;i++){
				kazu1[i]=0;
			}
			mojisu++;
			ct=0;
			string s[n][mojisu];
			for(int i=0;i<n;i++){
				int flg=1,len=in[i].size(),nanmoji=0;
				for(int j=0;j<len;j++){
					if(flg==1){
						s[i][nanmoji]=in[i][j];
						nanmoji++;
					}
					if(in[i][j]=='a'||in[i][j]=='i'||in[i][j]=='u'||in[i][j]=='e'||in[i][j]=='o'){
						flg=1;
					}
					else{
						flg=0;
					}
					if(nanmoji==mojisu){
						break;
					}
				}
				for(int j=nanmoji;j<mojisu;j++){
					s[i][j]='1';
					kazu1[i]=1;
				}
			}
			for(int i=0;i<n-1;i++){
				for(int j=i+1;j<n;j++){
					for(int k=0;k<mojisu;k++){
						if(s[i][k]!=s[j][k]){
							ct++;
							break;
						}
					}
				}
			}
			int b=0;
			for(int i=0;i<n;i++){
				b=b+kazu1[i];
			}
			if(b==n){
				mojisu=-1;
				break;
			}
		}
		cout<<mojisu<<endl;
	}
    return 0;
}

