int main() {
	while (true)
	{
		int n;
		cin >> n;
		if (n == 0) { break; }
		string ary[50];
		for (int i = 0; i < n; ++i) {
			cin >> ary[i];
		}


		int k = 1;
		int max = 0;
		bool founded = false;
		for (; k <= 50; k++) {
			set<string> st;
			bool allunique = true;

			for (int i = 0; i < n; ++i) {
				string code;
				bool flag = true;
				for (int j = 0; j < k; ++j) {
					if (j >= ary[i].length())
						break;
					char c = ary[i][j];
					if (flag) {
						code += c;
					}
					switch (c) {
					case 'a':
					case 'i':
					case 'u':
					case 'e':
					case 'o':
						flag = true;
						break;
					default:
						flag = false;
						break;
					}

				}

				auto itr = st.find(code);
				if (itr != st.end()) {
					allunique = false;
					break;
				}
				else {
					st.insert(code);
				}

			}

			if (allunique) {
				founded = true;

				for (auto a : st) {
					if (max < a.length())
						max = a.length();
				}
				break;
			}
		}

		if (founded) {
			cout << max << endl;
		}
		else
			cout << -1 << endl;
	}

}

