using namespace std;

string transportStr( string s )
{
    string res = "";
    res += s[0];

    for( int i = 1; i < s.size(); i++ )
        if( s[i-1] == 'a' || s[i-1] == 'i' || s[i-1] == 'u' || s[i-1] == 'e' || s[i-1] == 'o' )
            res += s[i];
    return res;
}

int main()
{
    int n;
    string s;

    while( cin >> n )
    {
        if( !n )
            break;

        string m[n];

        int min = 10000;
        for( int i = 0; i < n; i++ )
        {
            cin >> s;
            m[i] = transportStr( s );
            if( min > m[i].size() )
                min = m[i].size();
        }

        int k = 0;
        bool judge = true;
        while( judge && k <= 50 )
        {
            k++;
            judge = false;
            for( int i = 0; i < n - 1; i++ )
                for( int j = i + 1; j < n; j++ )
                    if( m[i].substr( 0, k ) == m[j].substr( 0, k ) )
                        judge = true;
        }
        if( k == 51 )
            cout << -1 << endl;
        else
            cout << k << endl;
    }
    return 0;
}

