int solve() {

	int n;
	cin >> n;

	if (!n) { return 0; }

	vector< string > s(n);
	vector< string > code(n);
	set<char> b = { 'a', 'i', 'u', 'e', 'o' };
	int maxlen = 0;


	// 入力を受け取る
	for (auto &str : s)
		cin >> str;



	// tdn表記
	for (int i = 0; i < n; i++) {

		code[i] += s[i][0];

		for (int j = 1; j < s[i].length(); j++) {
			if (b.find(s[i][j - 1]) != b.end())
				code[i] += s[i][j];
		}

	}

	for (auto c : code) { maxlen = max(maxlen, static_cast<int>(c.length())); }

	for (auto &c : code) {
		int d = maxlen - static_cast<int>(c.length());
		for (int i = 0; i < d+1; i++) {
			c += '*';
		}
	}

	int ans = -1;

	for (int i = 1; i < maxlen; i++) {

		if (ans > 0) { break; }

		for (int j = 0; j < n; j++) {
			for (int k = j + 1; k < n; k++) {

				int jlen = min(i, static_cast<int>(code[j].length()) - 1);
				int klen = min(i, static_cast<int>(code[k].length()) - 1);

				if (code[j].substr(0, jlen) == code[k].substr(0, klen)) {
					j = n; break;
				}

				if (k == n - 1) {
					ans = i;
				}

			}

		}

	}

	cout << ans << endl;

	return 1;
}

