using namespace std;

int diff(string a, string b) {
	if (a == b) {
		return -1;
	}
	int asz = a.size();
	int bsz = b.size();
	int Mmin = min(asz, bsz);
	int cc = 1;
	for (int i = 0; i < Mmin; i++) {
		if (a[i] == b[i]) {
			cc++;
		} else {
			break;
		}
	}
	return cc;
}

