				if (j+1 != s.size()) {
					if (s[j] == 'a' || s[j] == 'e' || s[j] == 'i' || s[j] == 'o' || s[j] == 'u') {
						b += s[j+1];
					}
				}
			}
			ss.push_back(b);
			//cout << b << endl;
		}
		sort(ss.begin(), ss.end());
		int MAX = 0;
		for (int i = 0; i+1 < ss.size(); i++) {
