int isVowel(char c){
	if(c=='a'||c=='i'||c=='u'||c=='e'||c=='o'){
		return 1;
	}
	else return 0;
}

string code(string str,int num){

	int cnt=1;
	string tmp = str.substr(0,1);

	for(int i=1;i<str.length()-1;i++){
		if(cnt==num)break;

		if(isVowel(str.at(i))){
			tmp += str.substr(i+1,1);
			cnt++;
		}
	}

	return tmp;

}

