int main(){
  while (1){
    int n;
    cin >> n;
    if (n == 0){
      break;
    }
    vector<string> s(n);
    for (int i = 0; i < n; i++){
      cin >> s[i];
    }
    vector<string> t(n);
    for (int i = 0; i < n; i++){
      int m = s[i].size();
      t[i] += s[i][0];
      for (int j = 0; j < m - 1; j++){
        if (s[i][j] == 'a' || s[i][j] == 'i' || s[i][j] == 'u' || s[i][j] == 'e' || s[i][j] == 'o'){
          t[i] += s[i][j + 1];
        }
      }
      t[i] += '#';
    }
    sort(t.begin(), t.end());
    int ans = -1;
    for (int i = 0; i < n - 1; i++){
      if (t[i] == t[i + 1]){
        ans = -1;
        break;
      } else {
        int m = min(t[i].size(), t[i + 1].size());
        for (int j = 0; j < m; j++){
          if (t[i][j] != t[i + 1][j]){
            ans = max(ans, j + 1);
            break;
          }
        }
      }
    }
    cout << ans << endl;
  }
}

