int main(){
  int n;
  char boin[6]={'a','i','u','e','o','A'};
  while(1){
    cin >> n;
    if(n==0) return 0;
    vector<string> s(n);
    bool flag;
    for(int i=0;i<n;i++){
      cin >> s[i];
      for(int j=1;j<s[i].length();j++){
        flag=false;
        for(int k=0;k<6;k++){
          if(s[i][j-1]==boin[k]){
            flag=true;
          }
        }
        if(!flag){
          flag=false;
          for(int k=0;k<6;k++){
            if(s[i][j]==boin[k]){
              flag=true;
            }
          }
          if(flag){
            s[i][j]='A';
          }else{
            s[i][j]='X';
          }
        }
      }
      for(int j=0;j<s[i].length();j++){
        if(s[i][j]=='A'||s[i][j]=='X'){
          s[i].erase(j,1);
          j--;
        }
      }
    }
    flag=false;
    for(int i=0;i<n;i++){
      for(int j=i+1;j<n;j++){
        if(s[i]==s[j]){
          cout << -1 << endl;
          flag=true;
        }
      }
    }
    int ans=0;
    if(!flag){
      for(int i=0;i<n;i++){
        for(int j=i+1;j<n;j++){
          flag=false;
          for(int k=0;k<min(s[i].length(),s[j].length());k++){
            if(s[i][k]!=s[j][k]){
              ans=max(ans,k+1);
              flag=true;
              break;
            }
          }
          if(!flag){
            ans=max(ans,min(s[i].length(),s[j].length())+1);
          }
        }
      }
      cout << ans << endl;
    }
  }
  return 0;
}

