        for(int i=1;i<=maxlen;i++){
            count = 0;
            for(int j=0;j<N;j++){
                for(int k=0;k<N;k++){
                    if(j!=k){
                        count += hikaku(cord[j],cord[k],i);
                    }
                }
            }
            if(count == 0){
                mini = i;
                break;
            }
        }

        if(mini == 0){
            mini = -1;
        }

        ans.push_back(mini);

    }

    for(int i=0;i<ans.size();i++){
        cout << ans[i] << endl;
    }
}

