using namespace std;

bool vowel(char c){
	return c == 'a' || c == 'i' || c == 'u' || c == 'e' || c == 'o';
}

void solve(int n){
	vector<string> v;
	for(int i=0; i<n; ++i){
		string s;
		cin >> s;
		string t;
		t.push_back(s[0]);
		for(int j=1; j<s.size(); ++j)
			if(vowel(s[j-1]))
				t.push_back(s[j]);
		v.push_back(t);
	}
//	for(int i=0; i<n; ++i)
//		cout << v[i] << "\n";
	sort(v.begin(), v.end());
	int ans = 1;
	for(int i=0; i+1<v.size(); ++i){
		if(v[i] == v[i+1]){
			ans = 100;
		}else{
			int tmp = v[i].size()+1;
			for(int j=1; j<=v[i].size(); ++j)
				if(v[i].substr(0, j) != v[i+1].substr(0, j)){
					tmp = j;
					break;
				}
			ans = max(ans, tmp);
		}
	}
	if(ans == 100) cout << -1 << "\n";
	else cout << ans << "\n";
}

int main(){
//	cin.tie(0);
//	ios::sync_with_stdio(false);
	while(1){
		int n;
		cin >> n;
		if(n == 0) return 0;
		solve(n);
	}
}

