#define INF 99999999

string name[60], dp[60];
int snum[60];

bool check(int n) {
  for (int i = 0; i < n; i++) {
    for (int j = i + 1; j < n; j++) {
      if(dp[i] == dp[j]) return false;
    }
  }
  return true;
}

bool all_check(int n) {
  for (int i = 0; i < n; i++) {
    if(snum[i] != INF) return true;
  }
  return false;
}

int solve(int n, int count) {
  L:
  if (!count) {
    for (int i = 0; i < n; i++) {
      dp[i]+=name[i][0];
      snum[i]++;
    }
    count++;
    if (check(n)) return count;
    else goto L;
  } else {
    for (int i = 0; i < n; i++) {
      if(snum[i] >= name[i].length()) continue;
