        snum[i]++;
        dp[i]+=name[i][snum[i]-1];
      } else {
        while (1) {
          tmp = name[i][snum[i]];
          if (tmp == 'a' || tmp == 'i' || tmp == 'u' || tmp == 'e' || tmp == 'o') break;
          snum[i]++;
        }
        snum[i]++;
        if (snum[i] < name[i].length()) dp[i]+=name[i][snum[i]];
        else snum[i] = INF;
      }
      if (!all_check(n)) return -1;
    }
    /*cout << "print dp: \n";
    for (int i = 0; i < n; i++) cout << dp[i] << endl;
    */count++;
    if (check(n)) return count;
    else goto L;
  }
  return -1;
}

