void solve(int N, const vector< string >& base) {	
	for (int k=1; k<55; ++k) {
		set< string > name_set;
		for (int i=0; i<N; ++i) {
			if ((int)base[i].size() <= k) name_set.insert(base[i]);
			else name_set.insert(base[i].substr(0, k));
		}
		if ((int)name_set.size() == N) {
			cout << k << endl;
			return;
		}
	}
	puts("-1");
}

