bool IsBoin(char c){
  return c=='a'||c=='i'||c=='u'||c=='e'||c=='o';
}

std::string MakeCode(std::string s){
  std::string result;

  result+=s[0];
  for(int i=0;i<s.size();i++){
    if(IsBoin(s[i])){
      if(i+1<s.size()){
        result+=s[i+1];
      }
    }
  }

  // cerr<<result<<endl;

  return result;
}

std::string MakeMini(std::string s,int k){
  std::string result;
  for(int i=0;i<k&&i<s.size();i++){
    result+=s[i];
  }
  return result;
}

int Solve(int n){
  std::vector<std::string> s(n);
  for(int i=0;i<n;i++){
    cin>>s[i];
  }

  std::vector<std::string> code(n);
  for(int i=0;i<n;i++){
    code[i]=MakeCode(s[i]);
  }

  for(int k=1;k<=50;k++){
    std::vector<std::string> minicode(n);
    for(int i=0;i<n;i++){
      minicode[i]=MakeMini(code[i],k);
    }
    bool isOK=true;
    for(int i=0;i<n;i++){
      for(int j=i+1;j<n;j++){
        if(minicode[i]==minicode[j]){
          isOK=false;
          break;
        }
      }
      if(!isOK) break;
    }
    if(isOK){
      return k;
    }
  }

  return -1;
}

int main(void) {
  cout << std::fixed << std::setprecision(10);
  cin.tie(0);
  std::ios::sync_with_stdio(false);

  while(1){
    int n;
    cin>>n;
    if(n==0){
      break;
    }
    cout<<Solve(n)<<endl;
  }

  return 0;
}

#include<bits/stdc++.h>
using namespace std;

string makeCode(string &s, int k){
  set<char> vowel = {'a', 'i', 'u', 'e', 'o'};
  string res = s.substr(0, 1);
  for (int i = 0; i < s.length() - 1; i++) {
    if(vowel.find(s[i]) == vowel.end())continue;
    res += s[i + 1];
  }
  return res.substr(0, k);
}

int main(){
  int n;
  while(std::cin >> n, n){
    vector<string> s(n);
    int mini = 1e9;
    for (int i = 0; i < n; i++) {
      std::cin >> s[i];
      mini = min(mini, (int)s[i].length());
    }
    int k = 0;
    bool f = true;
    while(f and k <= mini){
      k++;
      f = false;
      set<string> ss;
      for (int i = 0; i < n; i++) {
        string code = makeCode(s[i], k);
        //std::cout << code << std::endl;
        if(ss.find(code) != ss.end())f = true;
        ss.insert(code);
      }
    }
    if(k <= mini)std::cout << k << std::endl;
    else std::cout << -1 << std::endl;
  }
  return 0;
}

