using namespace std;
typedef long long ll;

int N;
string code[50];
int ans = 0;

void calc(int a, int b) {
	for (int i = 0; i < min(code[a].size(), code[b].size()); i++) {
		if (code[a][i] != code[b][i]) {
			ans = max(ans, i + 1);
			return;
		}
	}
	if (code[a].size() != code[b].size()) {
		int tmp = min(code[a].size(), code[b].size()) + 1;
		ans = max(ans, tmp);
	}else ans = 1000000;
}

int main() {
	while (1) {
		cin >> N;
		if (N == 0)break;
		for (int i = 0; i < N; i++) {
			string name;
			cin >> name;
			code[i] += name[0];
			for (int j = 0; j < name.size() - 1; j++) {
				if (name[j] == 'a' || name[j] == 'i' || name[j] == 'u' || name[j] == 'e' || name[j] == 'o') {
					code[i] += name[j + 1];
				}
			}
		}

		//パターンマッチをする
		for (int i = 0; i < N; i++) {
			for (int j = i + 1; j < N; j++) {
				calc(i, j);
			}
		}
		if (ans == 1000000)cout << -1 << endl;
		else cout << ans << endl;
		ans = 0;
		for (int i = 0; i < N; i++)code[i] = "";
	}
	return 0;
}

