bool isVowel(char c) {
    return c == 'a' || c == 'i' || c == 'u' || c == 'e' || c == 'o';
}

int solve(const int& n, const vector<string>& s) {
    vector<string> nukidashi;
    for (auto&& name : s) {
        bool use[name.size()] = {};
        use[0] = true;
        for (size_t i = 1; i < name.size(); i++) {
            use[i] = isVowel(name[i - 1]);
        }
        string namee = "";
        for (size_t i = 0; i < name.size(); i++) {
            if (use[i]) {
                namee += name[i];
            }
        }
        nukidashi.push_back(namee);
    }

    for (int k = 1;; k++) {
        bool great = true;
        set<string> codes;
        for (auto&& nukidash : nukidashi) {
            codes.insert(nukidash.substr(0, min((int)nukidash.size(), k)));
            great &= (nukidash.size() < k);
        }
        if (codes.size() == n) {
            return k;
        }
        if (great) {
            return -1;
        }
    }
}

