string change(string &s)
{
    string res;
    res += s[0];
    for (int i = 1; i < (int)s.size(); i++) {
        if (is_vowel(s[i-1])) {
            res += s[i];
        }
    }    
    return res;
}

bool check(vector<string> &t, int k, int N)
{
    set<string> st;
    for (int i = 0; i < (int)t.size(); i++) {
        if ((int)t[i].size() < k) {
            st.insert(t[i]);
        } else {
            st.insert(t[i].substr(0, k));
        }
    }
    return ((int)st.size() == N);
}

