int code_length(std::string str) {
	int ret = 1;
	for (int i = 0; i < str.length()-1; i++) {
		if ( str[i] == 'a' || str[i] == 'i' || str[i] == 'u' || str[i] == 'e' || str[i] == 'o' ) {
			ret++;
		}
	}
	return ret;
}

std::string trans_code(std::string str, int k) {
	std::string ret = "";
	ret += str[0];
	for (int i = 0; i < str.length()-1; i++) {
		if ( str[i] == 'a' || str[i] == 'i' || str[i] == 'u' || str[i] == 'e' || str[i] == 'o' ) {
			if ( ret.length() < k ) {
				ret += str[i+1];
			}
		}
	}
	return ret;
}

bool check_fact(std::vector< std::string > v) {
	int size = v.size();
	for (int i = 0; i < size-1; i++) {
		for (int j = i+1; j < size; j++) {
			if ( v[i] == v[j] ) {
				return false;
			}
		}
	}
	return true;
}

int main(void) {
	int n;
	cin >> n;
	while ( n != 0 ) {
		std::vector< std::string > name(n);
		int kmax = 1;
		int temp;
		for (int i = 0; i < n; i++) {
			cin >> name[i];
			temp = code_length(name[i]);
			if ( kmax < temp ) {
				kmax = temp;
			}
		}
		int ret = -1;
		bool find = false;
		std::vector< std::string > code(n);
		for (int i = 1; i < kmax+1 && !(find); i++) {
			for (int j = 0; j < n; j++) {
				code[j] = trans_code(name[j],i);
			}
			if ( check_fact(code) ) {
				ret = i;
				find = true;
			}
		}
		cout << ret << endl;
		cin >> n;
	}
	return 0;
}

