		cin >> n;
		if (n == 0) break;
		Code.clear();
		for (k = 0; k < n; k++){
			string temp, code;
			cin >> temp;
			code += temp[0];
			for (i = 0; i < temp.size() - 1; i++){
				if (temp[i] == 'a' || temp[i] == 'i' || temp[i] == 'u' || temp[i] == 'e' || temp[i] == 'o'){
					code += temp[i + 1];
				}
			}
			if (code[code.size() - 1] != '\0') code += '\0';
			Code.push_back(code);
			if (k == 0) max = code.size();
			else if (code.size() > max) max = code.size();
		}
		for (i = 0; i < n - 1; i++){
			for (j = i + 1; j < n; j++){
				int lmin;
				if (Code[i].size() < Code[j].size()) lmin = Code[i].size() - 1; else lmin = Code[j].size() - 1;
				for (k = 0; k < lmin; k++){
					if (Code[i][k] != Code[j][k]) break;
				}
				if (k == lmin){
					if (Code[i].size() == Code[j].size()) goto AllBreak;
				}
				if (i == 0 && j == 1) max = k;
				else if (k > max) max = k;
			}
		}
	AllBreak:
		if (i != n - 1 || j != n) cout << -1 << endl;
		else cout << max + 1 << endl;
	}
	return 0;
}

