using namespace std;

#define INF 99999999

string name[60], dp[60];
int snum[60];

bool check(int n) {
  for (int i = 0; i < n; i++) {
    for (int j = i + 1; j < n; j++) {
      if(dp[i] == dp[j]) return false;
    }
  }
  return true;
}

bool all_check(int n) {
  for (int i = 0; i < n; i++) {
    if(snum[i] != INF) return true;
  }
  return false;
}

int solve(int n, int count) {
  L:
  if (!count) {
    for (int i = 0; i < n; i++) {
      dp[i]+=name[i][0];
      snum[i]++;
    }
    count++;
    if (check(n)) return count;
    else goto L;
  } else {
    for (int i = 0; i < n; i++) {
      if(snum[i] == INF) continue;
      int tmp = name[i][snum[i]-1];
      if(count == 1 && (tmp == 'a' || tmp == 'i' || tmp == 'u' || tmp == 'e' || tmp == 'o')) {
        snum[i]++;
        dp[i]+=name[i][snum[i]-1];
      } else {
        while (1) {
          tmp = name[i][snum[i]];
          if (tmp == 'a' || tmp == 'i' || tmp == 'u' || tmp == 'e' || tmp == 'o') break;
          snum[i]++;
        }
        snum[i]++;
        if (snum[i] < name[i].length()) dp[i]+=name[i][snum[i]];
        else snum[i] = INF;
      }
      if (!all_check(n)) return -1;
    }
    /*cout << "print dp: \n";
    for (int i = 0; i < n; i++) cout << dp[i] << endl;
    */count++;
    if (check(n)) return count;
    else goto L;
  }
  return -1;
}

int main(void) {
  int n;
  cin.tie(0);
  //ios::sync_with_stdio(false);

  while (cin >> n, n) {
    memset(name, 0, sizeof(name));
    memset(dp, 0, sizeof(dp));
    fill(snum, snum + n, 0);
    for (int i = 0; i < n; i++) {
      name[i].reserve(60);
      dp[i].reserve(60);
    }
    for (int i = 0; i < n; i++) cin >> name[i];
    //for (int i = 0; i < n; i++) cout << name[i][6] << endl;
    cout << solve(n, 0) << endl;
  }
  return 0;
}

