int solve(int N) {
    vector<string> vs(N);
    char vowel[] = {'a', 'i', 'u', 'e', 'o'};
    for (int i = 0; i < N; i++) {
        string s; cin >> s;
        string parsed;
        for (int j = 0; j < s.length(); j++) {
            if (j == 0) {
                parsed.push_back(s[j]);
                continue;
            }
            for (int k = 0; k < 5; k++) {
                if (vowel[k] == s[j-1]) {
                    parsed.push_back(s[j]);
                    break;
                }
            }
        }
        vs[i] = parsed;
    }

    int ans = 0;
    for (int i = 0; i < N; i++) {
        for (int j = i+1; j < N; j++) {
            for (int k = 0; k < vs[i].length() && k < vs[j].length(); k++) {
                ans = max(k+1, ans);
                if (vs[i][k] != vs[j][k]) {
                    break;
                }
                if (k == vs[i].length() -1 || k == vs[j].length()) {
                    if (vs[i].length() == vs[j].length()) {
                        ans = 100;
                    } else {
                        ans = max(k+2, ans);
                    }
                }
            }
        }
    }

    if (ans == 100) ans = -1;
    return ans;
}

