  int n;
  cin.tie(0);
  //ios::sync_with_stdio(false);

  while (cin >> n, n) {
    memset(name, 0, sizeof(name));
    memset(dp, 0, sizeof(dp));
    fill(snum, snum + n, 0);
    for (int i = 0; i < n; i++) {
      name[i].reserve(60);
      dp[i].reserve(60);
    }
    for (int i = 0; i < n; i++) {
      cin >> name[i];
      cin.ignore();
    }
    //for (int i = 0; i < n; i++) cout << name[i][6] << endl;
    cout << solve(n, 0) << endl;
  }
  return 0;
}

